mapper.select:
SELECT a.ATCH_FILE_ID, b.FILE_CN, b.FILE_SN, b.FILE_STRE_COURS, b.STRE_FILE_NM, b.FILE_EXTSN, b.ORIGNL_FILE_NM, a.CREAT_DT, a.FILE_ID, b.FILE_SIZE, c.FILE_SIZE AS FILESIZE, c.FILE_UPLOAD_COUNT, c.FILE_PATH, c.FILE_URL, c.FILE_PREFIX, c.FILE_TYPE, c.FILE_WEB_DESC FROM LETTNFILE a, LETTNFILEDETAIL b, DM_UPLOAD_FILE_MASTER c WHERE
a.ATCH_FILE_ID = #{atchFileId,jdbcType=VARCHAR}
AND a.ATCH_FILE_ID = b.ATCH_FILE_ID AND a.FILE_ID = c.FILE_ID AND a.USE_AT = 'Y' ORDER BY b.FILE_SN

mapper.update:
UPDATE 
	LETTNFILEDETAIL 
SET 
	FILE_STRE_COURS 	= #{fileStreCours}
	,STRE_FILE_NM 		= #{streFileNm}
	,ORIGNL_FILE_NM		= #{orignlFileNm}
	,FILE_EXTSN			= #{fileExtsn}
	,FILE_SIZE			= #{fileMg}
	,FILE_CN			= #{fileCn}
WHERE
	ATCH_FILE_ID	=	#{atchFileId}
AND
	FILE_SN			=	#{fileSn}

mapper.delete:

DELETE FROM LETTNFILEDETAIL
WHERE
	ATCH_FILE_ID = #{atchFileId}
AND	
	FILE_SN = #{fileSn}

mapper.select:

SELECT COUNT(*)
FROM LETTNFILEDETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}


SELECT NVL(MAX(FILE_SN),0)+1 AS FILE_SN
FROM LETTNFILEDETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.update:

UPDATE LETTNFILE
SET USE_AT = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, TB.* FROM ( SELECT T1.FILE_ID AS "file_id", T1.FILE_TYPE AS "file_type", T1.FILE_DESC AS "file_desc", T1.CREATE_DATE AS "create_date", T1.FILE_SIZE AS "file_size", T1.FILE_UPLOAD_COUNT AS "file_upload_count", T1.FILE_PATH AS "file_path", T1.FILE_URL AS "file_url", T1.FILE_PREFIX AS "file_prefix", T1.FILE_WEB_DESC AS "file_web_desc" FROM DM_UPLOAD_FILE_MASTER T1 WHERE 1 = 1
AND T1.FILE_DESC LIKE '%게시판%'
AND T1.FILE_ID LIKE '%${searchKeyword}%'
AND T1.FILE_TYPE = #{file_type}
ORDER BY T1.FILE_ID, T1.CREATE_DATE ) TB ) TT WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:
UPDATE IDS SET
	NEXT_ID = NEXT_ID + 1
WHERE TABLE_NAME = #{table_name}
